calculo.calculo:
/*[type=jscript]*/ 

var contador;
var nbTable = $F.getValue("NBTABLA");

if (!nbTable) {
    $this.err = "¬No se puede crear el catálogo. Por favor, Cambie el nombre del catálogo";
}

if ($db.type == 'oracle') {
    nbTable = nbTable.toUpperCase();
} else if ($db.type == 'postgresql') {
    nbTable = nbTable.toLowerCase();
} 

contador = $db.count("CFD_TABLASYVISTAS", "TABLE_NAME", "WHERE TABLE_NAME = '" + nbTable + "'");
if (contador == 1) {
    $this.err = "¬La tabla ya existe. Cambie el nombre del catálogo";
}

/*[type=jscript]*/ 

//[@PGS!nombre]

var NbTabla = $F.getValue("NOMBRE");

if (!NbTabla) {
    $f.value = "";
} else {
    var BBDDtype = $db.type;
    var pref;
    var pref_tabla = "Cat";
    var CaracteresValidos = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789_";
    var Acentos = "áàâäÁÀÂÄéèêëÉÈÊËíìîïÍÌÎÏóòôöÓÒÔÖúùûüÚÙÛÜçÇñÑ";
    var SinAcentos = "aaaaAAAAeeeeEEEEiiiiIIIIooooOOOOuuuuUUUUcCnN";
    var numCaract;
    var NbTablaLimpio = "";

    // Prefijo que se añadirá al nombre de la tabla
    pref = $db.executeSql("select valor from OPCioNES where CLAVE = 'DESIGNER_PREFIJO_CATALOGOS'");
    if (!pref.isEmpty() && !pref.getValue(0,0)) pref_tabla = pref.getValue(0,0);

    // Adapta el nombre de la tabla sin caracteres extraños
    for (var i = 0; i < NbTabla.length; i++) {
        if(CaracteresValidos.indexOf(NbTabla.charAt(i)) >= 0) { 
            NbTablaLimpio += NbTabla.charAt(i);      
        } else if (Acentos.indexOf(NbTabla.charAt(i)) >= 0){
            NbTablaLimpio += SinAcentos.charAt(Acentos.indexOf(NbTabla.charAt(i)));      
        }
    }
    // En caso de que el nombre de la tabla quede vacio
    if (NbTablaLimpio.length == 0 && NbTabla.length > 0) {
        $f.value = "";
        $f.alertLevel = 1;
        $f.alert = "El nombre del catálogo no es válido";
        return;
    }
    // Trunca el nombre de la tabla en caso de superar el num. max. de caracteres
    if (BBDDtype.localeCompare("oracle") == 0) {
        numCaract = 30 - pref_tabla.length - 7; // .Nombre (length = 7)
    } else if (BBDDtype.localeCompare("postgresql") == 0) {
        numCaract = 128 - pref_tabla.length - 7;
    } else {
        // SQL Server
        numCaract = 63 - pref_tabla.length - 7; 
    }
    $sys.trace('numCaract: ' + numCaract);
    $sys.trace('NbTablaLimpio.length: ' + NbTablaLimpio.length);
    if (NbTablaLimpio.length > numCaract) {
        NbTablaLimpio = NbTablaLimpio.slice(0,numCaract);
        $sys.trace('NbTablaLimpio: ' + NbTablaLimpio);
    }

    // Nombre que finalmente tendrá la tabla
    NbTabla = pref_tabla + NbTablaLimpio.charAt(0).toUpperCase() + NbTablaLimpio.slice(1);
    $f.value = NbTabla;
}

/*[type=jscript]*/ 

