var Lista=null;
var Scr=""+ $F.getValue("sqlinit");
$sys.trace(Scr);

$this.HTML="";
if(!Scr) { return;}

try { Lista=$db.executeSql(Scr)}
catch(aError){
    var tErr=aError.message;
    tErr=tErr.split('\n').join('<br/>');
    $this.HTML="<div class='PWTextError resaltado' style='margin:30px; display:inline-block;'>Error:<br/>" + tErr +"</div>"
    return;
}
$this.HTML="<table class='table table-condensed PWTable PWHFix' style='min-width:500px; width:100%'><thead><tr class='PWTableHeader'><td class='PWTableCellHeader'>Id</td><td class='PWTableCellHeader'>Título</td></tr></thead><tbody>"
for (var i=0; i<Lista.size; i++){
    var aId=Lista.getValue(i,0);
    var aTx=Lista.getValue(i,1);
    $this.HTML+="<tr class='PWTableRow'><td class='PWTableCell' style='text-align:right;'>"+ aId +"</td><td class='PWTableCell'>" + aTx + "</td></tr>"
}
$this.HTML+="</tbody></table>"

