var NbTabla = $F.getValue("NBTABLA");

$sys.trace("IdForm: " + $sys.idForm);
$db.executeCommand("UPDATE CFD_TIPODATOS SET SqlInit = 'SELECT Id, Nombre AS TITULO FROM " + NbTabla + "' WHERE Id = " + $sys.idForm);

$sys.trace("NameBBDD: " + $db.type + "; VersionBBDD: " + $db.version);
if ($db.type == "oracle" && $db.version == '11') {
    $db.executeCommand('create table ' + NbTabla + '(Id number(10) NOT NULL, Nombre varchar2(255) NOT NULL, constraint PK_' + NbTabla + ' PRIMARY KEY(Id));');
    $db.executeCommand("CREATE SEQUENCE " + NbTabla + "_Id_seq START WITH 1;");
    $db.executeCommand("CREATE TRIGGER " + NbTabla + "_preIns_TR BEFORE INSERT ON " + NbTabla + " FOR EACH ROW BEGIN SELECT " + NbTabla + "_Id_seq.NEXTVAL INTO :new.Id FROM dual; END; --Fin");
} else if($db.type == "oracle" && $db.version == '12') {
    $db.executeCommand('create table ' + NbTabla + '(Id number(10) GENERATED ALWAYS AS IDENTITY, Nombre varchar2(255) NOT NULL, constraint PK_' + NbTabla + ' PRIMARY KEY(Id));');
} else if($db.type == "postgresql") {
    $db.executeCommand('create table ' + NbTabla + '(Id serial, Nombre varchar(255) NOT NULL, constraint PK_' + NbTabla + ' PRIMARY KEY(Id));');
} else {
    // SQL Server
    $db.executeCommand('create table dbo.' + NbTabla + '(Id int identity(1,1) NOT NULL, Nombre varchar(255) NOT NULL, constraint PK_' + NbTabla + ' PRIMARY KEY(Id));');
}

