calculo.calculo:
/*[type=jscript]*/ 

$sys.trace('idForm = ' + $sys.idForm);
var script = $db.executeSql("select value from CATALOGPRO where nombre = '" + $sys.idForm + "'").getStrAlt(0,0,'vacio');
$sys.trace('script = '+ script);

if(script == 'vacio'){
    $db.executeSql("update CATALOGPRO set tipo = 0 where nombre = '" + $sys.idForm + "'");
    return;
}

var typeScript = script.match(/type=css|type=jscript|type=sql/m);
if(typeScript){
    typeScript = typeScript[0].toString();
    $sys.trace('typeScript = ' + typeScript);
} else {
    $db.executeSql("update CATALOGPRO set tipo = 0 where nombre = '" + $sys.idForm + "'");
    return;
}

$sys.trace('indexOf = ' + script.indexOf(typeScript));
if(script.indexOf(typeScript) == 3){
    switch (typeScript) {
        case "type=jscript": 
            $db.executeSql("update CATALOGPRO set tipo = 0 where nombre = '" + $sys.idForm + "'");
            break;
        case "type=sql": 
            $db.executeSql("update CATALOGPRO set tipo = 1 where nombre = '" + $sys.idForm + "'");
            break;
        case "type=css": 
            $db.executeSql("update CATALOGPRO set tipo = 2 where nombre = '" + $sys.idForm + "'");
            break;
    }
} else {
    $db.executeSql("update CATALOGPRO set tipo = 0 where nombre = '" + $sys.idForm + "'");
}
