 createSQL ="CREATE TABLE WF_PROCESO_" + IdProceso + "(AGENDA bigint NOT NULL," +
 " CONSTRAINT PK_WF_PROCESO_" + IdProceso + " PRIMARY KEY (Agenda))";
 createConstraint="ALTER TABLE WF_PROCESO_"+ IdProceso +" ADD CONSTRAINT FK_WF_PROCESO_" + IdProceso + "_AG FOREIGN KEY(AGENDA) REFERENCES AGENDA (Id)";    
}
else{
 createSQL ="CREATE TABLE dbo.WF_Proceso_" + IdProceso + "(Agenda int NOT NULL," +
 " CONSTRAINT PK_WF_Proceso_" + IdProceso + " PRIMARY KEY CLUSTERED (Agenda ASC))";
 createConstraint="ALTER TABLE dbo.WF_Proceso_"+ IdProceso +" WITH CHECK ADD CONSTRAINT FK_WF_Proceso_" + IdProceso + "_AG FOREIGN KEY(Agenda) REFERENCES Agenda(Id)";
}
$db.executeCommand(createSQL);
