/*[type=jscript]*/

var id = $F.getValue('id');
if (!id) {
    var id = -1;    
}
var alias = $F.getValue('alias');
var contador = $db.count('SYS_PROCEDIMIENTOSWEBSERVICE', 'alias', 'Where Alias = \'' + alias + '\' and id <> ' + id); 
$sys.trace('Id: ' + id + '; Alias: ' + alias + '; Contador: ' + contador);

if (contador == 1) {
    $this.alertLevel = 0;
    $this.err = 'El campo Alias debe ser único';    
}
